export default function AllCheckbox(props) {
  return (
    <div className="connect-checkbox header-row-checkbox">
      <input
        id={props.allCheckboxId}
        className="all-checkbox"
        type="checkbox"
        checked={props.checked}
        onClick={props.allCheckboxChanged}
      />
      <label htmlFor={props.allCheckboxId} />
    </div>
  );
};
