import AllCheckbox from 'components/parentChildComponent/allCheckbox';
import SelectAllByFilters from 'components/parentChildComponent/selectAllByFilters';

function HeaderWrapper(props) {
  const {
    Header,
    headerOptions,
    showAllCheckbox,
    allCheckboxOptions,
    queryParams,
    updateQueryParams,
    showSelectAllByFilters
    } = props;

  const SelectAllByFiltersDisplay = (
    <SelectAllByFilters
      queryParams={queryParams}
      updateQueryParams={updateQueryParams}
    />
  );
  return (
    <div>
      <div className="parent-child-header-wrapper container-fluid clearfix ">
        {showAllCheckbox ? <AllCheckbox {...allCheckboxOptions} /> : null}
        <div className={"parent-child-header-content" + (showAllCheckbox ? " checkbox-showing" : "")}>
          <Header {...headerOptions} />
        </div>
      </div>
      {showSelectAllByFilters ? SelectAllByFiltersDisplay : null}
    </div>
  );
};

export default HeaderWrapper;
