import numeral from 'numeral';

export default function SelectAllByFilters(props) {
  if (props.queryParams.select_all_filtered) {
    return (
      <div className="select-all-filtered-container">
        {"All segments in this search are selected (" + numeral(props.queryParams.total_records).format() + " segments)."}
        <a onClick={props.updateQueryParams.bind(null, { select_all_filtered: false })}>
          &nbsp;Clear selection.
        </a>
      </div>
    );
  } else {
    return (
      <div className="select-all-filtered-container">
        All segments on this page are selected.
        <a onClick={props.updateQueryParams.bind(null, { select_all_filtered: true })}>
          &nbsp;Select all segments that match this search.
        </a>
      </div>
    );
  }
};
