export default function sortingCarets(props) {
  const caretUpActive = !props.sortedBy[props.attribute] || props.sortedBy[props.attribute] === 'asc';
  const caretDownActive = !props.sortedBy[props.attribute] || props.sortedBy[props.attribute] === 'desc';
  return (
    <div className="carets-container">
      <div className="top-caret">
        <i className={"fa fa-caret-up " + (caretUpActive ? "caret-active" : "caret-inactive")} />
      </div>
      <div className="bottom-caret">
        <i className={"fa fa-caret-down " + (caretDownActive ? "caret-active" : "caret-inactive")} />
      </div>
    </div>
  );
};
