export default function UiButtonGroup(props) {
  // User provides:
  //  - A hash which maps each button label to the function that gets called when it is clicked (labelToEventMap)

  const { labelToEventMap } = props;

  const buttons = _.map(Object.keys(labelToEventMap), (label) => {
    return (
      <div className="button lr-ui-button" key={label} onClick={labelToEventMap[label]}>{label}</div>
    );
  });

  return (
    <div className="lr-ui-button-group">
      {buttons}
    </div>
  );
}
