export default function UiFileUploadStatus(props) {
  // User provides:
  //  - progress: a percentage of the total work that the process has finished
  //  - text: optional text to have in the center of the component

  const { progress, text } = props;

  const correctProgress = progress/100 * 100.53;

  return (
    <div className="lr-ui-file-upload-status-container">
      <svg className="lr-ui-file-upload-status-svg" viewBox="0 0 32 32">
        { text ? <text x="50%" y="50%" className="lr-ui-file-upload-status-text">{text}</text> : null }
        <circle className="lr-ui-file-upload-status-progress-circle" r="16" cx="16" cy="16" strokeDasharray={correctProgress + " 100.53"}/>
        <circle className="lr-ui-file-upload-status-static-white-mask" r="16" cx="16" cy="16" />
        <circle className="lr-ui-file-upload-status-static-border" r="16" cx="16" cy="16" />
        <circle className="lr-ui-file-upload-status-progress-border" r="16" cx="16" cy="16" strokeDasharray={correctProgress + " 100.53"}/>
      </svg>
    </div>
  );

}
