export default function UiSwitch(props) {
  // user should pass in:
  // - a boolean, controlled by the parent, that says whether the switch is on or off (switchOn)
  // - change handling function that takes in the new state of the checkbox as an argument (handleChange) : function

  const { switchOn, handleChange } = props;

  const getSwitchMessage = (switchOn) => {
    let className = "switch-message"
    let message = switchOn ? " on" : " off"
    return(
      <div className={className + message}>{message}</div>
    );
  }

  const getSwitch = () => {
    return (
      <div className="switch-container" onClick={handleChange}>
        <div className="switch">
          <input
            className={switchOn ? "off-on-toggle on" : "off-on-toggle off"}
            type="checkbox"
            checked={switchOn}
            onChange={handleChange}
            />
          <label></label>
          {getSwitchMessage(switchOn)}
        </div>
      </div>
    );
  };

  return (
    <div className="lr-ui-switch">
      {getSwitch()}
    </div>
  );

};
