import UiIcon from './uiIcon.jsx';

export default function UiTile(props) {
  // User must pass in:
  //  - icon that is displayed in the tile (icon) : string
  //  - string displayed under the image as a label (labelText) : string
  //  - whether the tile is currently selected (selected) : boolean
  //  - function to update state in the parent component that takes in the
  //    change event (handleChange) : function
  //  - whether the tile is active (disabled) : boolean

  const { icon, labelText, selected, handleChange, disabled } = props;

  const handleClick = (e) => {
    if (!disabled) {
      handleChange(e);
    }
  };

  return (
    <div className={ cn('lr-ui-tile', {'selected': selected}, {'disabled': disabled})} onClick={handleClick}>
      <div className="lr-ui-tile-content">
        <UiIcon icon={icon} classes='lr-ui-tile-icon' />
        <div className="lr-ui-tile-text">{labelText}</div>
      </div>
    </div>
  )
};
