import UiAlert from '../components/uiAlert.jsx';
import UiIcon from '../components/uiIcon.jsx';

const UiAlertSandbox = React.createClass({
  getInitialState() {
    return ({
      alertType: 'success',
      alertMessage: 0,
      showAlert: true
    });
  },

  render() {
    return (
      <div>
        <div> blahb lah </div>
        blahblahblah
        <br/><br/><br/><br/>
        <button onClick={this.changeMessage}>change message</button>
        { this.state.showAlert ? <UiAlert
          type={this.state.alertType}
          message={this.state.alertMessage}
          permanent={false}
          handleClose={ () => { this.setState({showAlert: false}); } }
          /> : null }
      </div>
    );
  },

  changeMessage () {
    this.setState({alertMessage: 1 - this.state.alertMessage, showAlert: true});
  }

});

export default UiAlertSandbox;
