import UiHeader from '../components/uiHeader.jsx';
import UiSearch from '../components/uiSearch.jsx';

const UiHeaderSandbox = React.createClass({
  getInitialState() {
    return ({
      searchValue: ""
    });
  },

  render() {
    return (
      <div>
        <UiHeader
          textTitle={"Segments"}
          subHeader={<p>Showing<strong> 45 </strong>of<strong> 4000 </strong>segments</p>}
          rightSideContent={
            <UiSearch
              customStyle={{margin: '0'}}
              inputValue={this.state.searchValue}
              placeholder={"Search"}
              hideBorder={true}
              handleChange={(e) => { this.setState({searchValue: e.target.value}); } }
              />
            }
          />
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <UiSearch
          inputValue={this.state.searchValue}
          placeholder={"Search"}
          handleChange={(e) => { this.setState({searchValue: e.target.value}); } }
          />
      </div>

    );
  }
});
// textTitle='Segments'

export default UiHeaderSandbox;
