import UiSearch from '../components/uiSearch.jsx';

const UiSearchSandbox = React.createClass({
  getInitialState() {
    return ({
      inputValue: ""
    })
  },

  render() {
    return (
      <div style={{backgroundColor: "blue"}}>
        <UiSearch
          inputValue={this.state.inputValue}
          placeholder={"placeholder doe"}
          handleChange={ (e) => { this.setState({inputValue: e.target.value}); } }
          handleEnter={ () => { alert('enter doe'); } }
          />
      </div>
    )
  }

});

export default UiSearchSandbox;
