import UiSlider from '../components/uiSlider.jsx';

const sliderMap = {
  1: "Individual",
  2: "Household",
  3: "City",
  4: "State",
  5: "Country",
  6: "Planet",
  7: "Solar System"
};

const UiSliderSandbox = React.createClass({
  getInitialState() {
    return {
      sliderValue: 5
    }
  },

  render() {
    return (
      <div>
        <h4>UI SLIDER</h4>
        <UiSlider
          label={"Scope"}
          steps={sliderMap}
          value={this.state.sliderValue}
          handleChange={this.uiSliderChange}
          disabled={true}
        />
      </div>
    );
  },

  uiSliderChange(value) {
    this.setState({
      sliderValue: value
    });
  }
});

export default UiSliderSandbox;
