import UiTabbedView from 'components/uiTabbedView.jsx'

const TabbedViewContainer = React.createClass({
  getInitialState () {
    return {t: 0}
  },
  swap (tab, tabIndex) {
    console.log("tab", tab);
    console.log("tabIndex", tabIndex);
    this.setState({t: 1 - this.state.t});
  },
  render () {
    return (
      <UiTabbedView
        tabs={[['first', <p>first!</p>], ['second', <h1>second!</h1>]]}
        activeTabIndex={this.state.t}
        handleTabChange={this.swap} />
    );
  }
});

export default TabbedViewContainer;
