import UiTile from '../components/uiTile.jsx';

const UiTileParent = React.createClass({
  getInitialState: function() {
    return {
      selected: false
    }
  },
  handleTileClick: function(e) {
    console.log(e);
    this.setState({ selected: !this.state.selected });
  },
  render: function() {
    return (
      <UiTile
        icon='upload'
        labelText="Test Text"
        selected={this.state.selected}
        handleChange={this.handleTileClick}
        disabled={true}
      />
    )
  }
});

export default UiTileParent;
