import UiTitle from "../components/uiTitle.jsx"

const UiTitleSandbox = React.createClass({
	getInitialState() {
		return ({
			textTitle: "this is a uititle",
			switchOn: false
		})
	},
	toggleSwitch: function(e) {
		this.setState({ switchOn: !this.state.switchOn })
	},
	render() {
		return (
		<UiTitle
		textTitle={this.state.textTitle}
		date={true}
		showSwitch={true}
		switchOn={this.state.switchOn}
		handleChange={this.toggleSwitch}/>
		)
	}

});

export default UiTitleSandbox;